// hocs/withAuth.js
import { useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import { useUserContext } from 'contexts/UserContext';

export const useAuth = () => {
    const navigate = useNavigate();
    const { state } = useUserContext();

    useEffect(() => {
        if (!state.user) {
            navigate("/");
        }
    }, [state.user, router]);

    return state;
};
