import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

const DetailPagePet = () => {
  const [petData, setPetData] = useState(null);
  const navigate = useNavigate();

  const params = useParams();
  let petId = params.idPet;

  useEffect(() => {
    getPetData();
  }, []);

  const getPetData = async () => {
    try {
      const resultPetData = await axios.get(
        `http://localhost:8110/api/pet/get/${petId}`
      );

      if (!resultPetData.data) {
        // Si resultPetData.data es null, significa que la mascota no existe
        console.log("La mascota no existe");
        navigatePetList(); // Redirige a la lista de mascotas
        return;
      }
      setPetData(resultPetData.data);
    } catch (error) {
      console.error(error);
    }
  };

  const navigatePetList = () => {
    navigate("/pets");
  };

  return (
    <div>
      <div className="containerDetail">
        <div className="leftInfo">
          {/* Aquí puedes agregar cualquier información adicional que desees mostrar en la parte izquierda */}
        </div>
        <div className="rightInfo">
          <h3>About</h3>
          {petData ? (
            <>
              <p>Name: {petData.name}</p>
              <p>Type: {petData.type}</p>
              <p>Description: {petData.description}</p>
              <p>Skill 1: {petData.skill1 || "N/A"}</p>
              <p>Skill 2: {petData.skill2 || "N/A"}</p>
              <p>Skill 3: {petData.skill3 || "N/A"}</p>
              {/* Puedes mostrar más detalles según tus necesidades */}
            </>
          ) : (
            <p>Loading...</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default DetailPagePet;
