// UserContext.js
import React, { createContext, useContext, useReducer, useEffect } from 'react';
import axios from 'axios';


const INITIAL_STATE = {
    user: null,
    isLoading: true,
    error: null
};

const UserContext = createContext({
    state: INITIAL_STATE,
    dispatch: () => { }
});

const reducer = (state, action) => {
    switch (action.type) {
        case "SET_USER":
            return {
                ...state,
                user: action.payload,
                isLoading: false,
                error: null
            };
        case "CLEAR_USER":
            return {
                ...state,
                user: null,
                isLoading: false
            };
        case "SET_ERROR":
            return {
                ...state,
                error: action.payload,
                isLoading: false
            };
        default:
            return state;
    }
};


