import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./PageMovie.css";
const MoviePage = () => {
  
  const [listpets, setListpets] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchMovieList();

  }, []);

  const fetchMovieList = async () => {
    try {
      
      const resultpets = await axios.get("http://localhost:8110/api/movie/get");
      const sortedpets = resultpets.data.sort((a, b) =>
        a.title.localeCompare(b.title)
      );
      setListpets(sortedpets);
    } catch (error) {
      console.error(error);
    }
  };

  const deleteMovie = async (idMovie) => {
    try {
      const resultMovie = await axios.delete(
        `http://localhost:8110/api/movie/delete/${idMovie}`
      );

      if (resultMovie.status === 200) {
        const updatedpets = listpets.filter(
          (movie) => movie._id !== idMovie
        );
        setListpets(updatedpets);
        alert("Película eliminada correctamente");
      } else {
        alert("Error al eliminar la película");
      }
    } catch (error) {
      console.error(error);
    }
  };

  const addMovie = () => {
    navigate("/pets/addMovie");
  };

  const detailMovie = (idMovie) => {
    console.log(idMovie)
    navigate(`/pets/detailMovie/${idMovie}`);
  };
  const reviewMovie = (idMovie) => {
    console.log(idMovie)
    navigate(`/reviews/${idMovie}`);
  };

  const navigateToCrewBoard = () => {
    navigate("/pets");
  };

  return (
    <div>
      <div>
        <div className="header">
          <h1>Movie List</h1>
          <div className="buttonHeader">
            <button onClick={navigateToCrewBoard}>Crew Board</button>
            <button onClick={addMovie}>Add Movie</button>
          </div>
        </div>
        <div className="containerList">
          {listpets.map((movie, index) => (
            <div className="movie" key={index}>
              {/* <img src={movie.imageUrl} alt={movie.title} /> */}
              <div>
                <h3>{movie.title}</h3>
                <p>{movie.description}</p>
                <p>Rating: {movie.rating}</p>
                <button className="btnDetail" onClick={() => detailMovie(movie._id)}>
                  View Movie
                </button>
                <button className="btnDelete" onClick={() => deleteMovie(movie._id)}>
                  Delete Movie
                </button>
                <button className="btnDelete" onClick={() => reviewMovie(movie._id)}>
                  Revies Movie
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default MoviePage;
