import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import "./pirateDetailPage.css";

const PirateDetailPage = () => {
  const [pirateData, setPirateData] = useState({});
  const [stickLegs, setStickLegs] = useState(false);
  const [eyePatch, setEyePatch] = useState(false);
  const [handHook, setHandHook] = useState(false);

  const navigate = useNavigate();

  const params = useParams();
  let pirateId = params.idPirate;

  useEffect(() => {
    getPirateData();
  }, []);

  const getPirateData = async () => {
    let resultPirateData = await axios.get(
      "http://localhost:8110/api/pirate/get/" + pirateId
    );
    setPirateData(resultPirateData.data);
    setStickLegs(resultPirateData.data.stickLegs);
    setEyePatch(resultPirateData.data.eyePatch);
    setHandHook(resultPirateData.data.handHook);
  };

  const handleStickLegsClick = () => {
    setStickLegs(!stickLegs);
  };

  const handleEyePatchClick = () => {
    setEyePatch(!eyePatch);
  };

  const handleHandHookClick = () => {
    setHandHook(!handHook);
  };

  const navigatePirateCrew = () => {
    navigate("/pirates");
  };

  return (
    <div>
      <div className="headerdetail">
        <h1>{pirateData.namePirate}</h1>
        <button onClick={navigatePirateCrew}>Crew Board</button>
      </div>
      <div className="containerdetail">
        <div className="leftinfo">
          <img src={pirateData.imageUrl} alt={pirateData.name} />
          <h2>"{pirateData.pirateCatchPhrase}"</h2>
        </div>
        <div className="rigthinfo">
          <h3>About</h3>
          <p>Position: {pirateData.crewPosition}</p>
          <p>Treasures: {pirateData.numberTreasure}</p>
          <div className="check">
            <p>Stick Leg: {stickLegs ? "No" : "Yes"}</p>
            <button
              className={stickLegs ? "yes" : "no"}
              onClick={handleStickLegsClick}
            >
              {stickLegs ? "Yes" : "No"}
            </button>
          </div>
          <div className="check">
            <p>Eye Patch: {eyePatch ? "No" : "Yes"}</p>
            <button
              className={eyePatch ? "yes" : "no"}
              onClick={handleEyePatchClick}
            >
              {eyePatch ? "Yes" : "No"}
            </button>
          </div>
          <div className="check">
            <p>Hand Hook: {handHook ? "No" : "Yes"}</p>
            <button
              className={handHook ? "yes" : "no"}
              onClick={handleHandHookClick}
            >
              {handHook ? "Yes" : "No"}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PirateDetailPage;
