import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./PagePet.css";
import { Button, Paper }from '@mui/material/Button';

const PetPage = () => {
  const [listpets, setListpets] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchPetList();
  }, []);

  const fetchPetList = async () => {
    try {
      const resultpets = await axios.get("http://localhost:8110/api/pet/getPets");
      setListpets(resultpets.data);
    } catch (error) {
      console.error(error);
    }
  };

  const deletePet = async (idPet) => {
    try {
      const resultPet = await axios.delete(
        `http://localhost:8110/api/pet/deletePet/${idPet}`
      );

      if (resultPet.status === 200) {
        const updatedpets = listpets.filter(
          (pet) => pet._id !== idPet
        );
        setListpets(updatedpets);
        alert("Mascota eliminada correctamente");
      } else {
        alert("Error al eliminar la mascota");
      }
    } catch (error) {
      console.error(error);
    }
  };

  const addPet = () => {
    navigate("/pets/addPet");
  };

  const detailPet = (idPet) => {
    navigate(`/pets/detailPet/${idPet}`);
  };

  const reviewPet = (idPet) => {
    navigate(`/reviews/${idPet}`);
  };

  const navigateToCrewBoard = () => {
    navigate("/");
  };

  return (
    <div>
      <div>
        <div className="header">
          <h1>Lista de Mascotas</h1>
          <div className="buttonHeader">
            <Button variant="contained" color="primary" onClick={navigateToCrewBoard}>
              Crew Board
            </Button>
            <Button variant="contained" color="primary" onClick={addPet}>
              Agregar Mascota
            </Button>
          </div>
        </div>
        <div className="containerList">
          {listpets.map((pet, index) => (
            <Paper className="pet" elevation={3} key={index}>
              <div>
                <h3>{pet.name}</h3>
                <h3>{pet.type}</h3>
                <p>{pet.description}</p>
                <Button variant="outlined" color="primary" onClick={() => detailPet(pet._id)}>
                  Ver Mascota
                </Button>
                <Button variant="outlined" color="secondary" onClick={() => deletePet(pet._id)}>
                  Eliminar Mascota
                </Button>
                <Button variant="outlined" color="default" onClick={() => reviewPet(pet._id)}>
                  Rese√±as de Mascotas
                </Button>
              </div>
            </Paper>
          ))}
        </div>
      </div>
    </div>

  );
};

export default PetPage;
