import { useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";
import "./CreatePagePet.css";
import { useUserContext } from '../../../context/UserContext';
const PetCreatePage = () => {
  const [title, setTitle] = useState("");
  const [rating, setRating] = useState("");
  const [description, setDescription] = useState("");
  const { state } = useUserContext();
  const navigate = useNavigate();

  const createPet = async () => {
    if (title === "" || rating === "" || description === "") {
      alert("Complete los datos de la Película");
      return;
    }
    const { user } = state;
    const { _id } = user;
    let petObjData = {
      title: title,
      rating: rating,
      description: description,
      createdBy: _id, // Añadir el atributo createdBy

    };

    try {
      let responseCreate = await axios.post(
        "http://localhost:8110/api/pet/create",
        petObjData
      );

      if (responseCreate.status === 200) {
        const createdPetId = responseCreate.data._id;
        console.log(createdPetId)
        navigate("/pets/detailPet/" + createdPetId);
        setTitle("");
        setRating("");
        setDescription("");
      }
    } catch (e) {
      console.log(e.response.data.message);
      alert(e.response.data.message);
    }
  };

  const petList = () => {
    navigate("/pets");
  };

  return (
    <div>
      <div className="headerCreate">
        <h1>Add Pet</h1>
        <button onClick={petList}>Pet List</button>
      </div>
      <div className="containerPet">
        <div className="formleft">
          <label>Pet Title:</label>
          <br />
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <br />
          <label>Rating:</label>
          <br />
          <input
            type="number"
            value={rating}
            onChange={(e) => setRating(e.target.value)}
          />
          <br />
          <label>Description:</label>
          <br />
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <br />
        </div>
        <div className="formrigth">
          <button onClick={createPet}>Add Pet</button>
        </div>
      </div>
    </div>
  );
};

export default PetCreatePage;
