import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

const DetailPageMovie = () => {
  const [movieData, setMovieData] = useState({});
  // Añade el estado para el usuario creador si es necesario
  const navigate = useNavigate();

  const params = useParams();
  let movieId = params.idMovie;

  useEffect(() => {
    getMovieData();
  }, []);

  const getMovieData = async () => {
    try {
      console.log(movieId)
      const resultMovieData = await axios.get(
        "http://localhost:8110/api/movie/get/" + movieId
      );
      setMovieData(resultMovieData.data);
    } catch (error) {
      console.error(error);
    }
  };

  const navigateMovieList = () => {
    navigate("/movies");
  };

  return (
    <div>
      {/* ... (código del encabezado) ... */}

      <div className="containerDetail">
        <div className="leftInfo">
          {/* ... (código de la izquierda) ... */}
        </div>
        <div className="rightInfo">
          <h3>About</h3>
          <p>Title: {movieData.title}</p>
          <p>Rating: {movieData.rating}</p>
          <p>Description: {movieData.description}</p>
          {/* ... (código para mostrar información del usuario creador) ... */}
        </div>
      </div>
    </div>
  );
};

export default DetailPageMovie;
