import { useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";
import "./CreateReviewPage";

const CreateReviewPage = () => {
  const [numberTreasure, setNumberTreasure] = useState(0);
  const [description, setDescription] = useState("");
  const [movieId, setMovieId] = useState(""); // Assuming you have a way to get the Movie ID
  const navigate = useNavigate();

  const createReview = async () => {
    try {
      const reviewData = {
        numberTreasure,
        description,
        movie: movieId,
      };

      const resultCreateReview = await axios.post(
        "http://localhost:8110/api/review/create",
        reviewData
      );

      if (resultCreateReview.status === 200) {
        const createdReviewId = resultCreateReview.data._id;
        navigate(`/reviews/detailReview/${createdReviewId}`);
      }
    } catch (error) {
      console.error(error);
      alert("Error creating review");
    }
  };

  return (
    <div>
      <div className="headerCreate">
        <h1>Add Review</h1>
        <button onClick={() => navigate("/reviews")}>Review List</button>
      </div>
      <div className="containerReview">
        <div className="formLeft">
          <label>Rating (out of 10):</label>
          <br />
          <input
            type="number"
            value={numberTreasure}
            onChange={(e) => setNumberTreasure(e.target.value)}
          />
          <br />
          <label>Description:</label>
          <br />
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          ></textarea>
          <br />
          <button onClick={createReview}>Add Review</button>
        </div>
      </div>
    </div>
  );
};

export default CreateReviewPage;
