import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Pagereview.css";

const ReviewPage = () => {
  const [listReviews, setListReviews] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchReviewList();
  }, []);

  const fetchReviewList = async () => {
    try {
      const resultReviews = await axios.get("http://localhost:8110/api/review/get");
      setListReviews(resultReviews.data);
    } catch (error) {
      console.error(error);
    }
  };

  const deleteReview = async (idReview) => {
    try {
      const resultReview = await axios.delete(
        `http://localhost:8110/api/review/delete/${idReview}`
      );

      if (resultReview.status === 200) {
        const updatedReviews = listReviews.filter(
          (review) => review._id !== idReview
        );
        setListReviews(updatedReviews);
        alert("Revisi贸n eliminada correctamente");
      } else {
        alert("Error al eliminar la revisi贸n");
      }
    } catch (error) {
      console.error(error);
    }
  };

  const addReview = () => {
    navigate("/reviews/addReview");
  };

  const detailReview = (idReview) => {
    navigate(`/reviews/detailReview/${idReview}`);
  };

  const navigateToCrewBoard = () => {
    navigate("/movies");
  };

  return (
    <div>
      <div>
        <div className="header">
          <h1>Review List</h1>
          <div className="buttonHeader">
            <button onClick={navigateToCrewBoard}>Crew Board</button>
            <button onClick={addReview}>Add Review</button>
          </div>
        </div>
        <div className="containerList">
          {listReviews.map((review, index) => (
            <div className="review" key={index}>
              <h3>User: {review.user.firstName} {review.user.lastName}</h3>
              <p>Rating: {review.numberTreasure}/10</p>
              <p>Description: {review.description}</p>
              <button className="btnDetail" onClick={() => detailReview(review._id)}>
                View Review
              </button>
              <button className="btnDelete" onClick={() => deleteReview(review._id)}>
                Delete Review
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ReviewPage;
