// UserContext.js
import { createContext, useContext,useReducer, useState } from 'react';
import axios from 'axios';


const INITIAL_STATE = {
    user: null,
    isLoading: true,
    error: null
};

const UserContext = createContext({
    state: INITIAL_STATE,
    dispatch: () => { }
});

const reducer = (state, action) => {
    switch (action.type) {
        case "SET_USER":
            return {
                ...state,
                user: action.payload,
                isLoading: false,
                error: null
            };
        case "CLEAR_USER":
            return {
                ...state,
                user: null,
                isLoading: false
            };
        case "SET_ERROR":
            return {
                ...state,
                error: action.payload,
                isLoading: false
            };
        default:
            return state;
    }
};

export const UserProvider = ({ children }) => {
    const [user, setUser] = useState(null);

    const login = async (email, password, navigate) => {
        try {
            if (email === '' || password === '') {
                alert('Completar el formulario');
                return;
            }

            const objLogin = {
                email,
                password,
            };

            const resultLogin = await axios.post('http://localhost:8110/api/movie/login', objLogin);

            if (resultLogin.data === false) {
                alert('Mail o contraseña son incorrectos');
                return;
            }
            console.log(resultLogin)
            // Supongamos que result es el usuario obtenido después del inicio de sesión
            setUser(resultLogin.data);
            navigate('/movies');
        } catch (error) {
            console.error('Error al iniciar sesión:', error);
        }
    };

    return (
        <UserContext.Provider value={{ user, login }}>
            {children}
        </UserContext.Provider>
    );
};

export const useUser = () => {
    const context = useContext(UserContext);
    if (!context) {
        throw new Error('useUser must be used within a UserProvider');
    }
    return context;
};
