// hocs/withAuth.js
import { useEffect } from 'react';
import { useRouter } from 'next/router';
import { useUserContext } from 'contexts/UserContext';

export const useAuth = () => {
    const router = useRouter();
    const { state } = useUserContext();

    useEffect(() => {
        if (!state.user) {
            router.replace('/');
        }
    }, [state.user, router]);

    return state;
};
