// UserContext.js
import React, { createContext, useContext, useReducer, useEffect } from 'react';
import axios from 'axios';


const INITIAL_STATE = {
    user: null,
    isLoading: true,
    error: null
};

const UserContext = createContext({
    state: INITIAL_STATE,
    dispatch: () => { }
});

const reducer = (state, action) => {
    switch (action.type) {
        case "SET_USER":
            return {
                ...state,
                user: action.payload,
                isLoading: false,
                error: null
            };
        case "CLEAR_USER":
            return {
                ...state,
                user: null,
                isLoading: false
            };
        case "SET_ERROR":
            return {
                ...state,
                error: action.payload,
                isLoading: false
            };
        default:
            return state;
    }
};


export const UserProvider = ({ children }) => {
    const [state, dispatch] = useReducer(reducer, INITIAL_STATE);
    useEffect(() => {
        const fetchUser = async () => {
            try {
                const token = localStorage.getItem('tokenKey'); // Ajusta según cómo estés almacenando el token en tu frontend
                const id = localStorage.getItem('idUser'); // Ajusta según cómo estés almacenando el token en tu frontend
                if (!token) {
                    dispatch({ type: "CLEAR_USER" });
                    return;
                }

                const response = await axios.get(`http://localhost:8110/api/movie/authenticate/${id}`, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });

                const user = response.data.user; // Asegúrate de ajustar según la estructura de tu respuesta del backend

                dispatch({ type: "SET_USER", payload: user });
            } catch (error) {

                dispatch({ type: "CLEAR_USER" });
            }
        };

        fetchUser();
    }, [dispatch]);


    const login = async (email, password) => {
        try {
            if (email === '' || password === '') {
                alert('Completar el formulario');
                return;
            }

            const objLogin = {
                email,
                password,
            };

            const response = await axios.post('http://localhost:8110/api/movie/login', objLogin);

            const { data } = response;

            if (data.success) {
                // Almacena el token en el almacenamiento local
                localStorage.setItem('tokenKey', data.token); // Ajusta con la clave que deseas utilizar para almacenar el token
                localStorage.setItem('idUser', data.user._id); // Ajusta con la clave que deseas utilizar para almacenar el token

                // ... (otras acciones después del inicio de sesión)
            } else {
                alert('Mail o contraseña son incorrectos');
            }
            console.log('User after sign in:', data);
            console.log('User email after sign in:', data.email);

            // Aquí podrías extraer el token de la respuesta y almacenarlo en tu estado
            dispatch({ type: "SET_USER", payload: { user: data } });

            return { user: data };
        } catch (error) {
            console.error('Error in signIn function:', error);
            dispatch({ type: "SET_ERROR", payload: error.message });
            return { error };
        }
    };
    return (
        <UserContext.Provider value={{ state, login }}>
            {children}
        </UserContext.Provider>
    );
};

export const useUser = () => {
    const context = useContext(UserContext);
    if (!context) {
        throw new Error('useUser must be used within a UserProvider');
    }
    return context;
};
