// hocs/withAuth.js
import { useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import { useUser } from "../context/UserContext";

export const useAuth = () => {
    const navigate = useNavigate();
    const { state } = useUser();

    useEffect(() => {
        if (!state.user) {
            navigate("/");
        }
        else{
            navigate("/movies");
        }
    }, [navigate]);

    return state;
};



export const setTokenToLocal = (token) => {
    retulocalStorage.setItem('tu_clave_de_token', token); // Ajusta con la clave que deseas utilizar para almacenar el token
};
