// hocs/withAuth.js
import { useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import { useUserContext } from "../context/UserContext";

export const useAuth = () => {
    const navigate = useNavigate();
    const { state } = useUserContext();

    useEffect(() => {
        if (!state.user) {
            navigate("/");
        }
        else{
            navigate("/movies");
        }
    }, [navigate]);

    return state;
};
