// UserContext.js
import React, { createContext, useContext, useReducer } from 'react';
import axios from 'axios';

const UserContext = createContext();

const initialState = {
    user: null,
    isAuthenticated: false,
};

const userReducer = (state, action) => {
    switch (action.type) {
        case 'LOGIN':
            return {
                ...state,
                user: action.payload,
                isAuthenticated: true,
            };
        case 'LOGOUT':
            return {
                ...state,
                user: null,
                isAuthenticated: false,
            };
        default:
            return state;
    }
};

export const UserProvider = ({ children }) => {
    const [state, dispatch] = useReducer(userReducer, initialState);

    const login = async (email, password) => {
        try {
            const response = await axios.post('http://localhost:8110/api/movie/login', { email, password });
            const {  } = response;

            if (data.success) {
                dispatch({ type: 'LOGIN', payload: data.user });
            } else {
                // Manejar el error de inicio de sesión
                alert('Inicio de sesión fallido. Verifica tus credenciales e inténtalo de nuevo.');
            }
        } catch (error) {
            // Manejar el error de la solicitud
            alert('Error en la solicitud. Inténtalo de nuevo más tarde.');
        }
    };

    const logout = () => {
        dispatch({ type: 'LOGOUT' });
    };

    return (
        <UserContext.Provider value={{ state, login, logout }}>
            {children}
        </UserContext.Provider>
    );
};

export const useUserContext = () => useContext(UserContext);
