import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./PagePet.css";


const PetPage = () => {  
  const [listpets, setListpets] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchPetList();

  }, []);

  const fetchPetList = async () => {
    try {
      
      const resultpets = await axios.get("http://localhost:8110/api/pet/get");
      const sortedpets = resultpets.data.sort((a, b) =>
        a.title.localeCompare(b.title)
      );
      setListpets(sortedpets);
    } catch (error) {
      console.error(error);
    }
  };

  const deletePet = async (idPet) => {
    try {
      const resultPet = await axios.delete(
        `http://localhost:8110/api/pet/delete/${idPet}`
      );

      if (resultPet.status === 200) {
        const updatedpets = listpets.filter(
          (pet) => pet._id !== idPet
        );
        setListpets(updatedpets);
        alert("Película eliminada correctamente");
      } else {
        alert("Error al eliminar la película");
      }
    } catch (error) {
      console.error(error);
    }
  };

  const addPet = () => {
    navigate("/pets/addPet");
  };

  const detailPet = (idPet) => {
    console.log(idPet)
    navigate(`/pets/detailPet/${idPet}`);
  };
  const reviewPet = (idPet) => {
    console.log(idPet)
    navigate(`/reviews/${idPet}`);
  };

  const navigateToCrewBoard = () => {
    navigate("/pets");
  };

  return (
    <div>
      <div>
        <div className="header">
          <h1>Pet List</h1>
          <div className="buttonHeader">
            <button onClick={navigateToCrewBoard}>Crew Board</button>
            <button onClick={addPet}>Add Pet</button>
          </div>
        </div>
        <div className="containerList">
          {listpets.map((pet, index) => (
            <div className="pet" key={index}>
              {/* <img src={pet.imageUrl} alt={pet.title} /> */}
              <div>
                <h3>{pet.title}</h3>
                <p>{pet.description}</p>
                <p>Rating: {pet.rating}</p>
                <button className="btnDetail" onClick={() => detailPet(pet._id)}>
                  View Pet
                </button>
                <button className="btnDelete" onClick={() => deletePet(pet._id)}>
                  Delete Pet
                </button>
                <button className="btnDelete" onClick={() => reviewPet(pet._id)}>
                  Revies Pet
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default PetPage;
