import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

const DetailPagePet = () => {
  const [petData, setPetData] = useState(null);
  const [creatorData, setCreatorData] = useState(null);
  const navigate = useNavigate();

  const params = useParams();
  let petId = params.idPet;

  useEffect(() => {
    getPetData();
  }, []);

  const getPetData = async () => {
    try {
      const resultPetData = await axios.get(
        `http://localhost:8110/api/pet/get/${petId}`
      );

      if (!resultPetData.data) {
        // Si resultPetData.data es null, significa que la película no existe
        console.log("La película no existe");
        navigatePetList(); // Redirige a la lista de películas
        return;
      }
      let idUser = resultPetData.data.createdBy;
      setPetData(resultPetData.data);

      // Obtener información del creador si está disponible
      if (resultPetData.data.createdBy) {
        const resultCreatorData = await axios.get(
          `http://localhost:8110/api/pet`
        );
        
        setCreatorData(resultCreatorData.data);
      }
    } catch (error) {
      console.error(error);
    }
  };

  const navigatePetList = () => {
    navigate("/pets");
  };

  return (
    <div>
      {/* ... (código del encabezado) ... */}

      <div className="containerDetail">
        <div className="leftInfo">
          {/* ... (código de la izquierda) ... */}
        </div>
        <div className="rightInfo">
          <h3>About</h3>
          {petData ? (
            <>
              <p>Title: {petData.title}</p>
              <p>Rating: {petData.rating}</p>
              <p>Description: {petData.description}</p>
              {creatorData ? (
                <p>Created By: {creatorData.name}</p>
              ) : (
                <p>Creator information not available</p>
              )}
            </>
          ) : (
            <p>Loading...</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default DetailPagePet;
