// UserContext.js
import React, { createContext, useContext, useReducer, useEffect } from 'react';
import axios from 'axios';

const INITIAL_STATE = {
    user: null,
    isLoading: true,
    error: null
};

const UserContext = createContext({
    state: INITIAL_STATE,
    dispatch: () => { }
});

const reducer = (state, action) => {
    switch (action.type) {
        case "SET_USER":
            return {
                ...state,
                user: action.payload,
                isLoading: false,
                error: null
            };
        case "CLEAR_USER":
            return {
                ...state,
                user: null,
                isLoading: false
            };
        case "SET_ERROR":
            return {
                ...state,
                error: action.payload,
                isLoading: false
            };
        default:
            return state;
    }
};

export const UserProvider = ({ children }) => {
    const [state, dispatch] = useReducer(reducer, INITIAL_STATE);
    //const [someState, setSomeState] = useState(initialSomeState);
    useEffect(() => {
        // Comprobar la sesión de usuario al montar el componente
        const fetchUser = async () => {
            try {
                console.log("Inicio de sesión");
                const response = await axios.get('http://localhost:8110/api/movie/authenticate');  // Cambia la URL según tu endpoint
                const user = response.data;  // Ajusta según la estructura de tu respuesta
                dispatch({ type: "SET_USER", payload: user });
            } catch (error) {
                dispatch({ type: "CLEAR_USER" });
            }
        };

        fetchUser();
    }, [dispatch]);

    const login = async (email, password) => {
        try {
            if (email === '' || password === '') {
                alert('Completar el formulario');
                return;
            }
            const objLogin = {
                email,
                password
            };

            const { data } = await axios.post('http://localhost:8110/api/movie/login', objLogin);

            if (data.success === false) {
                alert('Mail o contraseña son incorrectos');
                return;
            }

            console.log('User after sign in:', data);
            console.log('User email after sign in:', data.email);

            // Aquí podrías extraer el token de la respuesta y almacenarlo en tu estado
            const token = data.token;
            dispatch({ type: "SET_USER", payload: { user: data, token } });

            return { user: data };
        } catch (error) {
            console.error('Error in signIn function:', error);
            dispatch({ type: "SET_ERROR", payload: error.message });
            return { error };
        }
    };

    return (
        <UserContext.Provider value={{ state, login }}>
            {children}
        </UserContext.Provider>
    );
};

export const useUser = () => {
    const context = useContext(UserContext);
    if (!context) {
        throw new Error('useUser must be used within a UserProvider');
    }
    return context;
};
