import { useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";
import "./CreatePageMovie.css";
import { useUserContext } from '../../../context/UserContext';
const MovieCreatePage = () => {
  const [title, setTitle] = useState("");
  const [rating, setRating] = useState("");
  const [description, setDescription] = useState("");
  const { state } = useUserContext();
  const navigate = useNavigate();

  const createMovie = async () => {
    if (title === "" || rating === "" || description === "") {
      alert("Complete los datos de la Película");
      return;
    }
    const { user } = state;
    const { _id } = user;
    let movieObjData = {
      title: title,
      rating: rating,
      description: description,
      createdBy: user._id, // Añadir el atributo createdBy

    };

    try {
      let responseCreate = await axios.post(
        "http://localhost:8110/api/movie/create",
        movieObjData
      );

      if (responseCreate.status === 200) {
        const createdMovieId = responseCreate.data._id;
        console.log(createdMovieId)
        navigate("/movies/detailMovie/" + createdMovieId);
        setTitle("");
        setRating("");
        setDescription("");
      }
    } catch (e) {
      console.log(e.response.data.message);
      alert(e.response.data.message);
    }
  };

  const movieList = () => {
    navigate("/movies");
  };

  return (
    <div>
      <div className="headerCreate">
        <h1>Add Movie</h1>
        <button onClick={movieList}>Movie List</button>
      </div>
      <div className="containerMovie">
        <div className="formleft">
          <label>Movie Title:</label>
          <br />
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <br />
          <label>Rating:</label>
          <br />
          <input
            type="number"
            value={rating}
            onChange={(e) => setRating(e.target.value)}
          />
          <br />
          <label>Description:</label>
          <br />
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <br />
        </div>
        <div className="formrigth">
          <button onClick={createMovie}>Add Movie</button>
        </div>
      </div>
    </div>
  );
};

export default MovieCreatePage;
