import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

const DetailPageMovie = () => {
  const [movieData, setMovieData] = useState(null);
  const [creatorData, setCreatorData] = useState(null);
  const navigate = useNavigate();

  const params = useParams();
  let movieId = params.idMovie;

  useEffect(() => {
    getMovieData();
  }, []);
  const fetchReviewData = async () => {
    try {
      const resultReviewData = await axios.get(
        `http://localhost:8110/api/review/get/${reviewId}`
      );
      setReviewData(resultReviewData.data);
    } catch (error) {
      console.error(error);
    }
  };

  const getMovieData = async () => {
    try {
      const resultMovieData = await axios.get(
        "http://localhost:8110/api/movie/get/"
      );

      if (!resultMovieData.data) {
        // Si resultMovieData.data es null, significa que la película no existe
        console.log("La película no existe");
        navigateMovieList(); // Redirige a la lista de películas
        return;
      }

      setMovieData(resultMovieData.data);

      // Obtener información del creador si está disponible
      if (resultMovieData.data.createdBy) {
        const resultCreatorData = await axios.get(
          "http://localhost:8110/api/user/get/" + resultMovieData.data.createdBy
        );
        setCreatorData(resultCreatorData.data);
      }
    } catch (error) {
      console.error(error);
    }
  };

  const navigateMovieList = () => {
    navigate("/movies");
  };

  return (
    <div>
      {/* ... (código del encabezado) ... */}

      <div className="containerDetail">
        <div className="leftInfo">
          {/* ... (código de la izquierda) ... */}
        </div>
        <div className="rightInfo">
          <h3>About</h3>
          {movieData ? (
            <>
              <p>Title: {movieData.title}</p>
              <p>Rating: {movieData.rating}</p>
              <p>Description: {movieData.description}</p>
              {creatorData ? (
                <p>Created By: {creatorData.name}</p>
              ) : (
                <p>Creator information not available</p>
              )}
            </>
          ) : (
            <p>Loading...</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default DetailPageMovie;
