// hocs/withAuth.js
import { useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import { useUser } from "../context/UserContext";

export const useAuth = () => {
    const navigate = useNavigate();
    const { state } = useUserz();

    useEffect(() => {
        if (!state.user) {
            navigate("/");
        }
        else{
            navigate("/movies");
        }
    }, [state.user, navigate]);

    return state;
};
