import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import "./Details.css";

const DetailReviewPage = () => {
  const [reviewData, setReviewData] = useState({});
  const navigate = useNavigate();

  const params = useParams();
  const reviewId = params.idReview;

  useEffect(() => {
    fetchReviewData();
  }, []);

  const fetchReviewData = async () => {
    try {
      const resultReviewData = await axios.get(
        `http://localhost:8110/api/review/get/${reviewId}`
      );
      setReviewData(resultReviewData.data);
    } catch (error) {
      console.error(error);
    }
  };

  const navigateToReviewList = () => {
    navigate("/reviews");
  };

  return (
    <div>
      <div className="headerDetail">
        <h1>Review Detail</h1>
        <button onClick={navigateToReviewList}>Review List</button>
      </div>
      <div className="containerDetail">
        <div className="leftInfo">
          <h3>User: {reviewData.user?.firstName} {reviewData.user?.lastName}</h3>
          <p>Rating: {reviewData.numberTreasure}/10</p>
          <p>Description: {reviewData.description}</p>
        </div>
      </div>
    </div>
  );
};

export default DetailReviewPage;
