import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./piratePage.css";

const PiratePage = () => {
  // setear los estados y metodos de navegación del react-router-dom
  const [listPirate, setListPirate] = useState([]);
  const navigate = useNavigate();

  // metodos para traer la lista
  useEffect(() => {
    ListApiPirate();
  }, []);

  const ListApiPirate = async () => {
    let resultPirate = await axios.get("http://localhost:8110/api/pirate/get");
    const listPirateOrder = resultPirate.data.sort((a, b) =>
      a.namePirate.localeCompare(b.namePirate)
    );
    setListPirate(listPirateOrder);
  };

  // metodo para borrar piratas
  const deletePirate = async (idPirate) => {
    let resultPirate = await axios.delete(
      "http://localhost:8110/api/pirate/delete/" + idPirate
    );

    if (resultPirate.status === 200) {
      let listPirateTemp = listPirate.filter(
        (pirate) => pirate._id !== idPirate
      );
      setListPirate(listPirateTemp);
      alert("Pirata Borrado de manera correcta");
    } else {
      alert("Error al borrar");
    }
  };

  // navegaciones a otras paginas con los botones
  const addPirate = () => {
    navigate("/pirates/addPirate");
  };

  const detailPirate = (idPirate) => {
    navigate("/pirates/detailPirate/" + idPirate);
  };

  const logOut = () => {
    navigate("/");
  };

  return (
    <div>
      <div>
        <div className="header">
          <h1>Pirate Crew</h1>
          <div className="buttonheader">
            <button onClick={logOut}>Logout</button>
            <button onClick={addPirate}>Add Pirate</button>
          </div>
        </div>
        <div className="containerlist">
          {listPirate.map((pirate, index) => {
            return (
              <div className="pirate" key={index}>
                <img src={pirate.imageUrl} alt={pirate.namePirate} />
                <div>
                  <h3>{pirate.namePirate}</h3>
                  <button
                    className="btndetail"
                    onClick={() => detailPirate(pirate._id)}
                  >
                    View Pirate
                  </button>
                  <button
                    className="btndelete"
                    onClick={() => deletePirate(pirate._id)}
                  >
                    Walk the Plank
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default PiratePage;
