import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./UserPage.css";
import { useUserContext } from '../../context/UserContext';
const RegisterUserPage = () => {
  
  const { login } = useUserContext();
  const { dispatch } = useUserContext(); // Obtén el dispatch desde el contexto

  const [firtsNameRegis, setFirtsNameRegis] = useState("");
  const [lastNameRegis, setLastNameRegis] = useState("");
  const [mailRegis, setMailRegis] = useState("");
  const [passwordRegis, setPasswordRegis] = useState("");
  const [confirmPasswordRegis, setConfirmPasswordRegis] = useState("");
  const [loading, setLoading] = useState(false);
  const [emailLogin, setEmailLogin] = useState("");
  const [passwordLogin, setPasswordLogin] = useState("");

  const navigate = useNavigate();

  const userRegister = async () => {
    if (
      firtsNameRegis === "" ||
      lastNameRegis === "" ||
      mailRegis === "" ||
      passwordRegis === "" ||
      confirmPasswordRegis === ""
    ) {
      alert("Por favor completar el formulario");
      return;
    }

    if (passwordRegis !== confirmPasswordRegis) {
      alert("Las contraseñas deben coincidir");
      return;
    }

    try {
      let objUser = {
        firstName: firtsNameRegis,
        lastName: lastNameRegis,
        email: mailRegis,
        password: passwordRegis,
        confirmPassword: confirmPasswordRegis,
      };
      let responseUser = await axios.post(
        "http://localhost:8110/api/pet/register",
        objUser
      );
      if (responseUser.status !== 200) {
        alert("Hubo un error al registrar el usuario");
        return;
      }
      alert("El registro fue exitoso");
      setFirtsNameRegis("");
      setLastNameRegis("");
      setMailRegis("");
      setPasswordRegis("");
      setConfirmPasswordRegis("");
    } catch (e) {
      console.log(e);
    }
  };

  const userLogin = async () => {
    try {
      setLoading(true);
      if (emailLogin === '' || passwordLogin === '') {
        alert('Completar el formulario');
        return;
      }
      const { user, error } = await axios.post('http://localhost:8110/api/pet/login', { emailLogin, passwordLogin });

      if (error) {
        handleLoginError(error);
        return;
      }

      if (user) {
        // Actualizar el estado global del usuario usando el Context API
        dispatch({ type: 'LOGIN', payload: user });

        // Navegar al dashboard del usuario si la sesión es exitosa
        navigate("/pets");
      } else {
        // Manejar caso de usuario no encontrado o sesión no iniciada
        handleUserNotFound();
      }
    } catch (error) {
      // Considera utilizar una función para manejar errores de forma centralizada
      handleLoginError(error);
    } finally {
      setLoading(false);
    }
  };
  function handleUserNotFound() {
    // Mostrar un mensaje adecuado al usuario
    alert("Usuario no encontrado. Por favor, verifique sus credenciales y vuelva a intentarlo.");
  }
  function handleLoginError(msg) {
    // Mostrar un mensaje adecuado al usuario
    alert(msg);
  }

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div>
      <div className="title">
        <h1>Welcome to Pet Crew</h1>
      </div>
      <div className="container">
        <div className="register">
          <h3>Register</h3>
          <form onSubmit={handleSubmit}>
            <label>First Name: </label>
            <br />
            <input
              type="text"
              value={firtsNameRegis}
              onChange={(e) => setFirtsNameRegis(e.target.value)}
            />
            <br />
            <label>Last Name: </label>
            <br />
            <input
              type="text"
              value={lastNameRegis}
              onChange={(e) => setLastNameRegis(e.target.value)}
            />
            <br />
            <label>Email: </label>
            <br />
            <input
              type="text"
              value={mailRegis}
              onChange={(e) => setMailRegis(e.target.value)}
            />
            <br />
            <label>Password: </label>
            <br />
            <input
              type="password"
              value={passwordRegis}
              onChange={(e) => setPasswordRegis(e.target.value)}
            />
            <br />
            <label>Confirm Password: </label>
            <br />
            <input
              type="password"
              value={confirmPasswordRegis}
              onChange={(e) => setConfirmPasswordRegis(e.target.value)}
            />
            <br />
            <br />
            <button onClick={userRegister}>Register</button>
          </form>
        </div>
        <div className="login">
          <h3>Login</h3>
          <form onSubmit={handleSubmit}>
            <label>Email: </label>
            <br />
            <input
              type="text"
              value={emailLogin}
              onChange={(e) => setEmailLogin(e.target.value)}
            />
            <br />
            <label>Password: </label>
            <br />
            <input
              type="password"
              value={passwordLogin}
              onChange={(e) => setPasswordLogin(e.target.value)}
            />
            <br />
            <br />
            <button onClick={userLogin} disabled={loading} >Login</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default RegisterUserPage;
