// UserContext.js
import React, { createContext, useContext, useReducer } from 'react';
import axios from 'axios';

const UserContext = createContext();

const initialState = {
    user: null,
    isAuthenticated: false,
    error: null, // Nuevo estado para manejar errores
};

const userReducer = (state, action) => {
    switch (action.type) {
        case 'LOGIN':
            return {
                ...state,
                user: action.payload.user,
                isAuthenticated: action.payload.isAuthenticated,
                error: null, // Limpiar cualquier error existente al iniciar sesión correctamente
            };
        case 'LOGOUT':
            return {
                ...state,
                user: null,
                isAuthenticated: false,
                error: null, // También limpiar errores al cerrar sesión
            };
        case 'SET_ERROR':
            return {
                ...state,
                error: action.payload,
            };
        default:
            return state;
    }
};

export const UserProvider = ({ children }) => {
    const [state, dispatch] = useReducer(userReducer, initialState);

    const login = async (email, password) => {
        try {
            const { data } = await axios.post('http://localhost:8110/api/movie/login', { email, password });
            const { user } = data.user
            if (data.success) {
                dispatch({ type: 'LOGIN', payload: { user: user, isAuthenticated: true } });
                return user
            } else {
                // Manejar el error de inicio de sesión
                dispatch({ type: 'SET_ERROR', payload: 'Inicio de sesión fallido. Verifica tus credenciales e inténtalo de nuevo.' });
                return error
            }
        } catch (error) {
            // Manejar el error de la solicitud
            dispatch({ type: 'SET_ERROR', payload: 'Error en la solicitud. Inténtalo de nuevo más tarde.' });
        }
    };

    const logout = () => {
        dispatch({ type: 'LOGOUT' });
    };

    return (
        <UserContext.Provider value={{ state, login, logout }}>
            {children}
        </UserContext.Provider>
    );
};

export const useUserContext = () => useContext(UserContext);
